  // -
  function Animal(name) {
      this.name = name;
  }

  Animal.prototype.sayName = function() {
      console.log(this.name, '-------->>>>>>>>Animal');
  }

  // --
  function Dog(name, age) {
      this.name = name;
      this.age = age;
  }

  // DogAnimal
  Dog.prototype = new Animal();
  // AnimalAnimal, DogAnimal
  Dog.prototype.sayName = function(argument) {
      console.log(`${this.name}(${this.age})---------->>>>>>>>>>>Dog`);
  }
  var dog = new Dog('', '7');
  dog.sayName();
